// allow inheritance
require("./inheritance");

/**
* @class Constants
* List of constants used in nuborn plugin.
*/
var Constants = {

	/**
	* When building app, you can build respectively : only app code, only libs code or app and libs code
	*/
	scopes: 
	{ 
		all:'all', 
		app:'app', 
		libs:'libs'
	},

	/**
	 * Specifics extentions which provoke a specific build.
	 */
	extensions:
	{
		all: "all",
		js: "js",
		hogan: "hogan",
		scss: "scss",
		html: "html"
	}
};

module.exports = Constants;